clips.content:
{{ form:create in="$0" }}

	{{ if errors }}
		<div>
			{{ errors }}
				{{ value }}<br>
			{{ /errors }}
		</div>
	{{ /if }}

	{{ if success }}
		<div>
			{{ success }}
		</div>
	{{ /if }}

	<label>Email</label>
	<input type="text" name="email" value="{{ old:email }}" />

	<label>Message</label>
	<textarea name="message" rows="5">{{ old:message }}</textarea>

	<button>Submit</button>

{{ /form:create }}
